mapper.select:
        select u.id,u.statistic, u.usercode, u.name,u.password,u.department, u.create_time, u.avatar, u.phone, u.email, u.sex, u.status, u.description, u.user_type,
               (select d.name from t_department d where d.id = u.department ) departmentName
        from t_user u
        where 1=1

          and (u.name like CONCAT('%',#{name},'%')  or u.usercode like CONCAT('%',#{name},'%'))


			and u.department = #{department}


          and usercode = #{usercode}


            and sex = #{sex}


            and status = #{status}

        and id!=1
        order by u.create_time desc
